namespace Mageplaza\Faqs\Model\Config\Source;

use Magento\Framework\Option\ArrayInterface;

/**
 * Class Visibility
 * @package Mageplaza\Faqs\Model\Config\Source
 */
class Visibility implements ArrayInterface
{
    const PUBLISH       = 1;
    const HIDDEN        = 0;
    const NEED_APPROVED = -1;

    /**
     * @return array
     */
    public function toOptionArray()
    {
        $options = [];
        foreach ($this->toArray() as $value => $label) {
            $options[] = [
                'value' => $value,
                'label' => $label
            ];
        }

        return $options;
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [self::PUBLISH => __('Public'), self::HIDDEN => __('Hidden'), self::NEED_APPROVED => __('Need Approved')];
    }
}
